import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { addToCart, filterItems,resetItemSelected } from './actions/cartActions'
class Navbar extends Component {

    searchQuery(event) {
        this.props.searchQuery(event.target.value);

    };

    resetItemSelected(state) {
     
        this.props.resetItemSelected(state);

    };

    render() {
        return (

            <nav className="nav-wrapper">

                <div className="container">
                <Link to="/" onClick={this.resetItemSelected(this.props)} className="brand-logo">Shopping</Link>
                        <input className="search-input" type="text" onChange={value => this.searchQuery(value)} placeholder="Search for items" />
                    <ul className="right">
                       
                        <li><Link to="/">Shop</Link></li>
                        <li><Link to="/cart">My cart {3}</Link></li>
                        <li><Link to="/cart"><i className="material-icons">shopping_cart</i></Link></li>

                    </ul>
                </div>
            </nav>
        )


    }
}




const mapStateToProps = (state) => {
    return {
        items: state.items,
        addedItems: state.addedItems

    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        searchQuery: (val) => dispatch(filterItems(val)),
        resetItemSelected: () => dispatch(resetItemSelected())
        
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Navbar)
