import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { addToCart, filterItems,resetItemSelected,setView } from './actions/cartActions'
class Navbar extends Component {

    searchQuery(event) {
        this.props.searchQuery(event.target.value);

    }

   
    handleGotoCartClick = (event) => {
        this.props.setView('cart');
    }

    handleContinueShoppingClick = (event) => {
        this.props.setView('home');
    }


    

    render() {
        let totalItemCount=0;
        this.props.addedItems.forEach(item => {
            totalItemCount+=item.quantity
        });
        return (

            <nav className="nav-wrapper">

                <div className="container">

                <Link className='continue-shopping' to="/" onClick={this.handleContinueShoppingClick}>Continue shopping</Link>
                        <input className="search-input" type="text" onChange={value => this.searchQuery(value)} placeholder="Search for items" />
                    <ul className="right">
                       
                        <li><Link to="/" onClick={this.handleGotoCartClick}>My cart [{totalItemCount}] </Link></li>
                        <li><Link to="/"><i className="material-icons">shopping_cart</i></Link></li>

                    </ul>
                </div>
            </nav>
        )


    }
}




const mapStateToProps = (state) => {
    return {
        items: state.items,
        addedItems: state.addedItems

    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        searchQuery: (val) => dispatch(filterItems(val)),
        setView: (val) => dispatch(setView(val)),
        
        
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Navbar)
