import React, { Component } from 'react';
import { connect } from 'react-redux'
import { addToCart, itemSelected } from './actions/cartActions'
import { useHistory } from "react-router-dom";
import ItemDetail from './ItemDetail';

class Home extends Component {
    
    handleClick = (id) => {
        this.props.addToCart(id);
    }

    handleImageClick = (id) => {
        this.props.itemSelected(id);
      
    }

    render() {

        let itemList = this.props.items.map(item => {
            return (
                <div className="card" key={item.id}>

                    <div className="card-image">
                        <a href="/">
                            <img src={item.img} alt={item.title} onClick={() => { this.handleImageClick(item.id) }} />

                        </a>
                        <span className="card-title">{item.title}</span>
                        <span to="/" className="btn-floating halfway-fab waves-effect waves-light red" onClick={() => { this.handleClick(item.id) }}><i className="material-icons">add</i></span>
                    </div>

                    <div className="card-content">
                        <p>{item.desc}</p>
                        <p><b>Price: {item.price}$</b></p>
                    </div>
                </div>

            )
        })

        return (
           this.props.itemSelected? <ItemDetail/> :
            <div className="container">
                <h3 className="center">Our items</h3>
                <div className="box">
                    {itemList}
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        items: state.items,
        itemSelected:state.itemSelected


    }
}


const mapDispatchToProps = (dispatch) => {

    return {
        addToCart: (id) => { dispatch(addToCart(id)) },
        itemSelected: (id) => { dispatch(itemSelected(id)) }

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Home)
