import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { addToCart,filterItems } from './actions/cartActions'
class Navbar extends Component {

    searchQuery(event) {
        this.props.searchQuery({ value: event.target.value });
     console.log(event.target.value);
    };

    render() {
        return (
            <nav className="nav-wrapper">

                <div className="container">
                    <Link to="/" className="brand-logo">Shopping</Link>
                    <input type="text" onChange={value=> this.searchQuery(value)} placeholder="Search for items" />
                    <ul className="right">
                        <li><Link to="/">Shop</Link></li>
                        <li><Link to="/cart">My cart {this.props.addedItems.length}</Link></li>
                        <li><Link to="/cart"><i className="material-icons">shopping_cart</i></Link></li>

                    </ul>
                </div>
            </nav>
        )


    }
}




const mapStateToProps = (state) => {
    return {
        items: state.items,
        addedItems: state.addedItems

    }
}
const mapDispatchToProps = (dispatch) => {
    return {
        searchQuery: (val) => dispatch(filterItems(val))
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Navbar)
