import React, { Component } from 'react';
import { connect } from 'react-redux'
import { addToCart } from './actions/cartActions'

class ItemDetail extends Component {

    handleClick = (id) => {
        this.props.addToCart(id);
    }

    render() {

        let selectedItem = this.props.items.filter(item => item.id === this.props.selectedId)[0];
        console.log(selectedItem);
        return (
            <div className="card" key={selectedItem.id}>

                <div className="card-image">
                    <img src={selectedItem.img} alt={selectedItem.title} />
                    <span className="card-title">{selectedItem.title}</span>
                    <span to="/" className="btn-floating halfway-fab waves-effect waves-light red" onClick={() => { this.handleClick(selectedItem.id) }}><i className="material-icons">add</i></span>
                   
                </div>

                <div className="card-content">
                    <p>{selectedItem.desc}</p>
                    <p><b>Price: {selectedItem.price}$</b></p>
                </div>
            
                <input placeholder='Quantity'  type="number" min="1" />    
                  
              
            </div>

        )


        return (
            <div className="container">
                <h3 className="center">Quantity</h3>
                <div className="box">
                    <input type="number" min="1" />
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        items: state.items,
        selectedId: state.selectedId

    }
}


const mapDispatchToProps = (dispatch) => {

    return {
        addToCart: (id) => { dispatch(addToCart(id)) }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ItemDetail)
